import React, { useEffect, useState } from 'react';
import { Divider, Card, CardContent, Grid, Button, CardActions, Hidden } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { customURL } from '../../../utils/helperFunctions';
import { customTranslateCampaign } from '../../../utils/customTranslate';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';

const styles = theme => ({
  cardContent: {
    textAlign: 'center',
    padding: '40px !important',
  },
  titleMargin: {
    margin: '20px auto 20px auto',
    [theme.breakpoints.down('sm')]: {
      marginTop: 0,
    },
  },
  backBtnText: {
    color: theme.palette.secondary.main,
    '&:hover': {
      textDecoration: 'none',
      color: theme.palette.primary.main,
    },
  },
  backBtnContainer: {
    marginTop: 2,
  },
});

const DashboardComponent = ({ data, viewSelectedAd, changeCampaign, classes, campaigns }) => {
  const { t } = useTranslation('campaigns', 'adDetails');
  const [jobsToRender, setJobsToRender] = useState([]);
  const selectedPage = 1;
  const dispatch = useDispatch()
  const {jobPostNumber} = useSelector((state) => state.jobs)

  useEffect(() => {
    axios.get(`https://localhost:7262/jobsEn/${jobPostNumber}`).then(res => {
      setJobsToRender(res.data);
      console.log(jobsToRender);
    });
  }, []);

  return (
    <div>
          <div className="container"  
          key={item.jobPostNumber}
          >
      <div className={classes.backBtnContainer}>
        <Link to="/omat-ilmoitukseni" className={classes.backBtnText}>
          <ArrowBackIosIcon /> {t('jobs:backButton')}
        </Link>
      </div>

      <div className={classes.titleMargin}>
        <h2 className="ad_title_1">{t('adDetails:dashboard')} </h2>
        <h6 className="ad_title_2">
          <strong style={{ marginRight: 10 }}>
            {item.jobName},{' '}
            {item.jobPostAsukohaAddress.map(address => {
              {
                if (address.address[17]) {
                  return address.address
                    .split(',')
                    .splice(1)
                    .toString();
                } else return address.address;
              }
            })}
          </strong>
        </h6>
        <Divider />
      </div>
      <div className="ad_title_3">
        <Grid container spacing={1}>
          <Grid item>
            <h3>{t('campaign')}: </h3>
          </Grid>
          <Grid item>
            <h3>{customTranslateCampaign('Free')}</h3>
          </Grid>
        </Grid>
        <strong style={{ color: '#34495e' }}>
          {item && item.dateOfApplication}
        </strong>{' '}
      </div>
      <div>
        <Card>
          <CardContent>
            <Grid container justifyContent="space-around" alignItems="center" spacing={1}>
              <Grid item>
                <div>
                  <h5>{t('campaignType')}</h5>
                </div>
                <div>
                  <h3 className="ad_title_1">{customTranslateCampaign('Free')}</h3>
                </div>
              </Grid>
              <CardActions>
                <Grid item sm={12}>
                  <Link to={customURL(item.url, 'campaign')} className="btnLink">
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => changeCampaign(campaigns.find(campaign => campaign.id === item.campaignType))}
                    >
                      {t('adDetails:boostBtn')}
                    </Button>
                  </Link>
                </Grid>
              </CardActions>
            </Grid>
          </CardContent>
        </Card>
      </div>
      <div style={{ marginTop: 30 }}>
        <Grid container spacing={2}>
          <Grid item sm={6} xs={12}>
            <Card>
              <CardContent className={classes.cardContent}>
                <div>
                  <h3>
                    <strong>{item.totalLikes}</strong>
                  </h3>
                </div>
                <div>{t('adDetails:totalFavs')}</div>
              </CardContent>
            </Card>
          </Grid>
          <Grid item sm={6} xs={12}>
            <Card>
              <CardContent className={classes.cardContent}>
                <div>
                  <h3>
                    <strong>{item.totalViewed}</strong>
                  </h3>
                </div>
                <div>{t('adDetails:totalViewed')}</div>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </div>
      <div style={{ marginTop: 30 }}>
        <Grid container spacing={2}>
          <Grid item sm={6} xs={12}>
            <Card>
              <CardContent className={classes.cardContent}>
                <div>
                  <h3>
                    <strong>{'2'}</strong>
                  </h3>
                </div>
                <div>{t('adDetails:totalLinkClicks')}</div>
              </CardContent>
            </Card>
          </Grid>
          <Grid item sm={6} xs={12}>
            <Card>
              <CardContent className={classes.cardContent}>
                <div>
                  <h3>
                    <strong>{item.totalApplicants}</strong>
                  </h3>
                </div>
                <div>{t('adDetails:totalApplication')}</div>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </div>

      <Hidden xsDown>
        <div style={{ marginTop: 30 }}>
          <Grid container spacing={2}>
            <Grid item sm={12}>
              <Card>
                <CardContent className={classes.cardContent}>
                  <div>
                    <h3>{t('adDetails:chartTitle')}</h3>
                  </div>
                  <div>
                    <Line data={data} />
                  </div>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </div>
      </Hidden>
      {/* For responsive charts */}
      {/*  <Hidden smUp>
        <div style={{ marginTop: 30 }}>
          <h3>{t('adDetails:chartTitle')}</h3>

          <Line data={data} />
        </div>
      </Hidden> */}
      <div style={{ height: 60 }}></div>
    </div>
