import React, { useEffect, useState } from 'react';
import { Button, Grid, Divider, Snackbar, Hidden } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';
import DescriptionIcon from '@material-ui/icons/Description';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { MySnackbarContentWrapper } from '../../utils/snackbar.utils';
import { FavBtn } from '../../utils/favBtn.js';
import { convertJobHoursToStr, convertJobTypeToStr, convertJobWorksStartToStr, customURL } from '../../utils/helperFunctions';
import CustomizedDialogs from '../../utils/customizedDialog';
import NotFoundPage from '../../utils/notFoundPage';
import Loader from '../../utils/loader';
//import { isRteEmpty } from '../../containers/validate';
import { SEO } from '../seo/metaInfo.component';
import { mol_page_url } from '../../constants';
import axios from 'axios';

const styles = theme => ({
  addMargin: {
    margin: '20px auto 20px auto',
  },
  backBtnContainer: {
    margin: '16px auto',
  },
  backBtnText: {
    color: theme.palette.secondary.main,
    '&:hover': {
      textDecoration: 'none',
      color: theme.palette.primary.main,
    },
  },

  companyImage: {
    minHeight: 420,
    maxHeight: 600,
    maxWidth: '100%',
    [theme.breakpoints.down('xs')]: {
      minHeight: 190,
      maxHeight: 190,
      width: '100%',
    },
    [theme.breakpoints.only('sm')]: {
      minHeight: 360,
      maxHeight: 420,
      width: '100%',
    },
  },
  companyImgFrame: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },

  companyLogo: {
    minHeight: 80,
    minWidth: 150,
    backgroundSize: 'contain',
    backgroundRepeat: 'no-repeat',
  },
  jobDetail: {
    margin: '20px 0 50px 0',
    padding: '0 0 50px',
    border: '1px solid lightgray',
  },
  jobDesc: {
    padding: '20px 40px',
  },
  additionalInfo: {
    padding: '10px 40px',
  },
  sourceInfo: {
    padding: '10px 40px',
    float: 'right',
  },
  metaData: {
    color: theme.palette.secondary.main,
  },
  metaDataTitle: {
    color: theme.palette.primary.main,
  },

  ctaBtn: {
    marginBottom: 20,
    [theme.breakpoints.up('md')]: {
      justifyContent: 'flex-end',
      marginBottom: 50,
    },
  },
});

const JobDetailsComponent = ({
  jobDetails,
  workStart,
  toggleFavoriteJobs,
  favBtnstatus,
  showSuccessSnackbar,
  showFailedSnackbar,
  closeSnackbar,
  showDialog,
  showSpinner,
  classes,
  changePagination,
}) => {
  const [jobsToRender, setJobsToRender] = useState([]);
  useEffect(() => {
    axios.get('https://localhost:7262/jobsEn').then(res => {
      setJobsToRender(res.data);
      console.log(jobsToRender);
    });
  },[]);

  const { t } = useTranslation('jobDetails', 'jobhours', 'jobtype');
  const heroImage = jobsToRender.logo && jobsToRender.logo[0].path;
  const title = `${jobsToRender?.jobName} - ${jobsToRender?.companyName}, ${jobsToRender?.jobPostAsukohaAddress.address} | Avoimet tyÃ¶paikat`;

  return (
    <div>
        <h1>Test</h1>
      <Loader showSpinner={showSpinner} />

      <Snackbar
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        open={showSuccessSnackbar}
        autoHideDuration={2000}
        onClose={() => {
          closeSnackbar();
        }}
      >
        <MySnackbarContentWrapper
          variant="success"
          message={t('successMsg')}
          onClose={() => {
            closeSnackbar();
          }}
        />
      </Snackbar>
      <Snackbar
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        open={showFailedSnackbar}
        autoHideDuration={2000}
        onClose={() => {
          closeSnackbar();
        }}
      >
        <MySnackbarContentWrapper
          onClose={() => {
            closeSnackbar();
          }}
          variant="error"
          message={t('failedMsg')}
        />
      </Snackbar>

      <CustomizedDialogs showDialog={showDialog} dialogText={t('loginText')} loginModal />
    </div>
  );
};

export default withStyles(styles)(JobDetailsComponent);
